import React, { useState } from "react";

const Portfolio = () => {
  // State to keep track of which modal is open (AboutModal or ProjectModal)
  const [openModal, setOpenModal] = useState(null);

  const handleOpenModal = (modal) => {
    setOpenModal(modal);
  };

  const handleCloseModal = () => {
    setOpenModal(null);
  };
  return (
    <div className="App">
      {/* Navigation Bar */}
      <div className="navbar">
        <nav>
          <ul>
            <li>
              <a href="#about" onClick={() => handleOpenModal("About")}>
                About
              </a>
            </li>
            <li>
              <a href="#projects" onClick={() => handleOpenModal("Projects")}>
                Projects
              </a>
            </li>
            <li>
              <a href="#contact" onClick={() => {}}>
                Contact
              </a>
            </li>
          </ul>
        </nav>
        <h1>Your Name</h1>
      </div>

      {/* Page Sections */}
      <section id="about" className="about-section">
        <h2>About</h2>
        {/* Add content about yourself here */}
      </section>
      <section id="projects" className="project-section">
        <h2>Projects</h2>
        {/* Project Cards */}
        {[1, 2, 3, 4].map((project) => (  // Display some sample project cards. You can generate more dynamically as needed
          <div key={project} className="project-card" onClick={() => handleOpenModal("Project")}>
            <h3>Project {project}</h3>
            <p>A brief description of the project...</p>
          </div>
        ))}
      </section>
      <section id="contact" className="contact-section">
        <h2>Contact</h2>
        {/* Add contact information here */}
      </section>

      {/* Modals */}
      {openModal === "About" && (
        <AboutModal onClose={handleCloseModal} />
      )}
      {openModal === "Projects" && (
        <ProjectModal onClose={handleCloseModal} />  
      )}
    </div>
  );
};

// Modal components (AboutModal and ProjectModal)
const AboutModal = ({ onClose }) => {
  return (
    <div className="modal">
      <div className="modal-content">
        <h2>About Me</h2>
        <p>Add more details about yourself here...</p>
        <button onClick={onClose}>Close</button>
      </div>
    </div>
  );
};

const ProjectModal = ({ onClose }) => {
  return (
    <div className="modal">
      <div className="modal-content">
        <h2>Project Details</h2>
        {/* Display detailed project information here */}
        <button onClick={onClose}>Close</button>
      </div>
    </div>
  );
};
export default Portfolio;
