import React, { useState } from "react";
import AppBar from "./AppBar";
import About from "./About";
import Project from "./Project";
import Contact from "./Contact";

function Portfolio() {
  const [openModal, setOpenModal] = useState(false);
  const [activeModal, setActiveModal] = useState("");

  const handleModalOpen = (modal) => {
    setOpenModal(true);
    setActiveModal(modal);
  };

  const handleModalClose = () => {
    setOpenModal(false);
  };
  return (
    <div className="App">
      <AppBar handleModalOpen={handleModalOpen} />
      <div className="main-content">
        <div id="about" className="section">
          <h2>About</h2>
          {/* Add content for the About section */}
        </div>
        <div id="projects" className="section">
          <h2>Projects</h2>
          {/* Add buttons to open project modals */}
          <button onClick={() => handleModalOpen("project1")}>
            Project 1
          </button>
          <button onClick={() => handleModalOpen("project2")}>
            Project 2
          </button>
          {/* Add more project buttons as needed */}
        </div>
        <div id="contact" className="section">
          <h2>Contact</h2>
          {/* Add content for the Contact section */}
        </div>
      </div>

      {openModal && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h3>{activeModal === "about" ? "About" : "Project"}</h3>
              <button onClick={handleModalClose}>Ã—</button>
            </div>
            <div className="modal-body">
              {activeModal === "about" ? <About /> : <Project />}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Portfolio;
